import React, { useState } from "react";
import "./Card.css";

const Card = () => {
    const [state, setState] = useState(0); // 0 = face down, 1 = face up, 2 = shifted left

    const handleClick = () => {
        setState((prev) => (prev < 2 ? prev + 1 : 2)); // Increment state up to 2
    };

    return (
        <div className="card-container">
            <div
                className={`card ${state === 1 ? "flipped" : ""} ${state === 2 ? "shifted" : ""
                    }`}
                onClick={handleClick}
            >
                {state === 0 || state === 2 ? (
                    <div className="card-back"> {/* Back of the card */}
                        <img
                            src="/path-to-your-purple-card.png"
                            alt="Card Back"
                            className="card-image"
                        />
                    </div>
                ) : (
                    <div className="card-front"> {/* Front of the card */}
                        <p>Your professional development question here!</p>
                    </div>
                )}
            </div>
        </div>
    );
};

export default Card;
