import React, { useState } from "react";
import "./Card.css";

const CardGame = () => {
    const [cards, setCards] = useState([{ id: 0, flipped: false }]);
    const [currentCard, setCurrentCard] = useState(0);

    const handleClick = () => {
        const updatedCards = [...cards];

        // Toggle flipped state
        if (!updatedCards[currentCard].flipped) {
            updatedCards[currentCard].flipped = true;
        } else {
            // Shift to the next card and add a new one
            updatedCards[currentCard].shifted = true;
            const newCard = { id: cards.length, flipped: false };
            updatedCards.push(newCard);
            setCurrentCard(currentCard + 1);
        }

        setCards(updatedCards);
    };

    return (
        <div className="card-container">
            {cards.map((card, index) => (
                <div
                    key={card.id}
                    className={`card ${card.flipped ? "flipped" : ""} ${card.shifted ? "shifted" : ""
                        }`}
                    style={{
                        zIndex: cards.length - index,
                    }}
                    onClick={index === currentCard ? handleClick : null}
                >
                    {card.flipped ? (
                        <div className="card-front">
                            <p>Question #{card.id + 1}</p>
                        </div>
                    ) : (
                        <div className="card-back">
                            <p>Card Back</p>
                        </div>
                    )}
                </div>
            ))}
        </div>
    );
};

export default CardGame;
